import { createContext, useContext, useEffect, useState } from "react";
import Cookies from 'js-cookie';
import { useNavigate } from "react-router-dom";
import axios from "../Api/axios";

const AuthContext = createContext({});

export const AuthProvider = ({ children }) => {

    const [user, setUser] = useState(null);
    const [err, setErrors] = useState("");
    // const csrf = () => axios.get("/sanctum/csrf-cookie");
    const navigate =  useNavigate();

    const getUser = async () => {

        try {

            //const token = Cookies.get('token');
            const { data } = await axios.get('/api/user',
            {headers: {
                'Authorization': `Bearer ${Cookies.get('token')}`
            }});
            setUser(data);

        }catch (e){

        }
    }

    const login = async({ ...data }) => {

        // await csrf();

        try {
            const login = await axios.post('/api/login', { ...data });
            Cookies.set('token', login.data.token, { expires: 7, secure: true });
            await getUser();
            navigate("/");
        } catch (error) {

            if(error.response.status === 401){
                setErrors(error.response.data.error);
            }

        }

    }

    const logout = async () => {

        await axios.post('/api/logout', null,
            {
                headers: {
                'Authorization': `Bearer ${Cookies.get('token')}`
                }
            }).then(() => {
                setUser(null);
                Cookies.set('token', '', { expires: -1, secure: true });
            });
    }

    useEffect(()=>{
        if(!user){
            getUser();
        }
    },[])

    return <AuthContext.Provider value={{ user, err, getUser, login, logout}}>
        { children }
    </AuthContext.Provider>

}

export default function useAuthContext(){
    return useContext(AuthContext);
}
