import React from 'react';
import { useState } from 'react';
import useAuthContext from '../context/AuthContext';
import { NumericFormat } from 'react-number-format';

export const Deposit = () => {

  const [valor, setValor] = useState(0);
  const { deposit, depositErr } = useAuthContext();

  const handleSubmit = async (e) => {
    e.preventDefault();
    deposit({valor});
  }

  const handleChange = value => {
    setValor(value);
  };

  return (
  <div className="flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
    <div className="max-w-md w-full space-y-8">
      <div>
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          Faça um depósito na sua conta
        </h2>
      </div>
      <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
        <div className="rounded-md shadow-sm -space-y-px">
          <label htmlFor="valor">Valor a depositar</label>
          <NumericFormat value={value} allowLeadingZeros thousandSeparator="," />;
            {
              depositErr && <div>{ depositErr}</div>
            }
        </div>

          <button
            type="submit"
            className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Depositar
          </button>
      </form>
    </div>
  </div>
  )
}
