import React, { useEffect } from 'react';
import { Link } from "react-router-dom";
import { useState } from 'react';
import Cookies from 'js-cookie';
import axios from '../../Api/axios';
import { HiCurrencyDollar } from "react-icons/hi";

export const Index = () => {

  const [users, setUsers] = useState([]);

  useEffect(() => {


    const fetchUsers = async () => {
      try {

          const getusers = await axios.get('/api/users',
          {
              headers: {
                  'Authorization': `Bearer ${Cookies.get('token')}`
              }
          });
          setUsers(getusers.data);

      } catch (error) {

          console.log(error);

      }
    };

    fetchUsers();
  }, []); 

  return (
    <>
    <div className="bg-blue-500 text-white p-2">Listando usuários cadastrados</div>
    <table className="w-full table-auto">
      <thead>
        <tr className="bg-gray-600 text-white uppercase text-sm leading-normal">
          <th className="py-3 px-6 text-left">Usuário</th>
          <th className="py-3 px-6 text-center">Saldo</th>
          <th className="py-3 px-6 text-left"></th>
        </tr>
      </thead>
      <tbody className="text-gray-600 text-sm font-light">
    {users.map(user => (
      <tr key={user.id} className="border-b border-gray-200 hover:bg-gray-100">
        <td className="py-3 px-6 text-left whitespace-nowrap">
          <div className="flex items-center space-x-4 rtl:space-x-reverse">
            <div className="flex-shrink-0">
                <img className="w-8 h-8 rounded-full" src="https://www.w3schools.com/howto/img_avatar.png" />
            </div>
            <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-gray-900 truncate dark:text-white">
                  {user.name}
                </p>
                <p className="text-sm text-gray-500 truncate dark:text-gray-400">
                  {user.email}
                </p>
            </div>
          </div>
        </td>
        <td className="py-3 px-6 text-center whitespace-nowrap">
            <div className="inline-flex items-center text-base font-semibold text-gray-900 dark:text-white">
                R$ { (user.account ? user.account?.value : '0,00')}
            </div>
        </td>
        <td className="py-3 px-6 text-center whitespace-nowrap">
            <Link to="/deposito" className="m-2 p-2 bg-blue-500 hover:bg-indigo-700 text-white rounded-md"><HiCurrencyDollar />Depositar</Link>
            <HiCurrencyDollar /><Link to="/transferir" className="m-2 p-2 bg-blue-500 hover:bg-indigo-700 text-white rounded-md">Transferir</Link>
        </td> 
      </tr>
    ))}
    </tbody>
   </table>
   </>
  )
}
